INSERT INTO `t_content` VALUES ('16', '<p style=\"margin-left:0cm;\"><b><span style=\"font-family:宋体;\">国家重点基础研究发展计划（</span></b><b><span>973</span></b><b><span style=\"font-family:宋体;\">项目）资助</span></b></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">认知无线网络体系结构与协议研究（项目编号：</span><span>2009CB320401</span><span style=\"font-family:宋体;\">）</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">编码调制协同传输理论（项目编号：</span><span>2007CB310604</span><span style=\"font-family:宋体;\">）</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">认知无线网络基础理论与关键技术研究</span></p>\r\n<p style=\"margin-left:0cm;\"><b><span style=\"font-family:宋体;\">国家高技术研究发展计划（</span></b><b><span>863</span></b><b><span style=\"font-family:宋体;\">）项目</span></b></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">远距离通信中编码调制与同步的联合优化，（项目编号：</span><span> 2009AA01Z233</span><span style=\"font-family:宋体;\">）</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">频谱感知与干扰评估新技术研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">脱机手写汉字的建立与高精度识别算法的研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">手写汉字识别系统的研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">银行票据</span><span>OCR</span><span style=\"font-family:宋体;\">系统中若干关键技术的研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">演进型网络多媒体过滤器</span><span>EMF</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——Gbps </span><span style=\"font-family:宋体;\">无线传输关键技术与试验系统研究开发</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">新型蜂窝网络体系结构</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">新型无线网络体系结构下的宽带</span><span>MIMO</span><span style=\"font-family:宋体;\">信道的建模和仿真方法研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">基于部分信道信息的高效</span><span>MIMO</span><span style=\"font-family:宋体;\">传输技术研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">环境自适应高效多用户多天线协同传输与控制技术</span></p>\r\n<p style=\"margin-left:0cm;\"><b><span style=\"font-family:宋体;\">国家自然科学基金资助项目</span></b></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">未来无线网络的分布式处理理论研究（项目编号：</span><span> 60772108</span><span style=\"font-family:宋体;\">）</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">宽带移动通信系统中迭代同步理论与算法研究（项目编号：</span><span> 60702048</span><span style=\"font-family:宋体;\">）</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">非有限域上的广义低密度生成阵码理论与应用研究（项目编号：</span><span> 60902048</span><span style=\"font-family:宋体;\">）</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">基于时域频谱利用概率分布曲线拟合的频谱检测研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">无线多媒体内容分发网络中关键技术的研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">基于融合熵准则与独立元分析的残缺指纹识别技术研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">视频交互在异构网络中客户体验保障方法研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">具有感知能力的自适应可重配置</span><span>Ad hoc</span><span style=\"font-family:宋体;\">网络</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">一种新型无线通信系统关键技术及其电磁兼容的研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">基于序列偶的超宽带认知无线电系统研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">认知无线</span><span>MESH</span><span style=\"font-family:宋体;\">网络路由研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">多天线中继协作传输系统中的空时信号处理与联合优化</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">协同</span><span>MIMO</span><span style=\"font-family:宋体;\">通信跨层联合预编码抑制干扰研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">异构</span><span>IP</span><span style=\"font-family:宋体;\">网络中基于多目标决策理论的资源优化技术研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">自主端到端重构无线网络的体系结构及其关键技术</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">广义小区环境下多载波用户</span><span>MIMO</span><span style=\"font-family:宋体;\">系统中的联合信道估计及导频设计技术</span></p>\r\n<p style=\"margin-left:0cm;\"><b><span style=\"font-family:宋体;\">国家自然科学基金青年基金项目</span></b></p>\r\n<p style=\"text-indent:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">极化域频谱感知理论与技术研究</span></p>\r\n<p style=\"margin-left:0cm;\"><b><span style=\"font-family:宋体;\">国家科技重大专项</span></b></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——IMT-Advanced</span><span style=\"font-family:宋体;\">新型无线接入网络技术研发（项目编号：</span><span>2008ZX03003-005</span><span style=\"font-family:宋体;\">）</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——TD-LTE TTCN</span><span style=\"font-family:宋体;\">规范研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">传感器网络与移动通信网的结合技术及其</span><span>M2M</span><span style=\"font-family:宋体;\">业务应用研究与示范验证</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——LTE</span><span style=\"font-family:宋体;\">业务应用及网络演进研究</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">超宽带设备的技术规范和性能评测</span></p>\r\n<p style=\"margin-left:0cm;\"><b><span style=\"font-family:宋体;\">国家科技支撑计划</span></b></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">面向</span><span>IMT-Advanced</span><span style=\"font-family:宋体;\">标准化无线网络关键技术研究（项目编号：</span><span>2008BAH30B10</span><span style=\"font-family:宋体;\">）</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">移动数据采集系统</span></p>\r\n<p style=\"margin-left:0cm;\"><b><span style=\"font-family:宋体;\">教育部博士点基金项目</span></b></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">端到端重构网络中的联合业务调度及频谱交易机制研究</span><span>--</span><span style=\"font-family:宋体;\">博士点基金</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">自适应同频协同宽带无线传输技术研究</span><span>--</span><span style=\"font-family:宋体;\">博士点基金</span></p>\r\n<p style=\"margin-left:21.0pt;\"><span>——</span><span style=\"font-family:宋体;\">一种超级无线通信新技术及其电磁兼容的研究</span></p>', '在研项目', '', '2014-12-18 10:41:01', '', '0', null, '8', null, '14');
