INSERT INTO `t_content` VALUES ('27', '<p>&nbsp;信息与通信工程学院是北京邮电大学规模最大的学院，也是目前国内规模最大、设施齐全、基础雄厚的信息通信专业人才培养基地之一。</p>\r\n<p></p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;学院拥有一支以我国信息理论研究领域的开拓者和奠基人、中国工程院周炯槃院士为学术委员会名誉主任，以中国工程院院士刘韵洁教授为院长，以多位国内外知名专家为学科带头人、以高水平的博士生导师、教授、副教授和讲师的组成的师资队伍。学院现有院士2名，博士生导师39名，硕士生导师137名，具有博士学位的教师比例达到72%。学院以信息通信为特色，重点建设的一级学科“信息与通信工程”被评为国家级重点学科；学院科研实力雄厚。近年来承担完成了大量国家自然科学基金、国家攻关、国家863计划、国家973计划和省部级等多项重点科研项目，也承担完成了大批横向科研任务。</p>\r\n<p></p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;学院根据现代信息社会和通信技术发展的迫切要求，在信息通信领域的各个层面上培养不同层次的高科技人才。学院目前有全日制本科生、硕士研究生、博士研究生在校学生5800余人，工程硕士研究生在读生1400余人，近几年来，学院分别在山东、山西、河南、河北、广东、广西、内蒙古、福建、黑龙江、新疆、深圳、天津、北京等地与移动通信公司、网通公司、邮校、研究生工作站等单位合作建立基地，进行合作办学培养工程硕士，为保证质量，学院选派教学效果好责任心强的教师前往基地教学，实行学校与企业双导师制对学生进行论文指导，与合作单位加强管理，为各个班级设立专人进行教学管理，目前，已获得工程硕士学位有800余人。为我国信息与通信领域的发展做出了很大的贡献。</p>\r\n<p></p>\r\n<br />', '基本情况', '', '2014-12-14 17:11:22', '', '0', null, '0', null, '107');
INSERT INTO `t_content` VALUES ('25', '<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">我院兹定于</span><span style=\"font-family:Arial, sans-serif;\">2012</span><span style=\"font-family:宋体;\">年</span><span style=\"font-family:Arial, sans-serif;\">2</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">13</span><span style=\"font-family:宋体;\">日开展接收</span><span style=\"font-family:Arial, sans-serif;\">2011</span><span style=\"font-family:宋体;\">级转专业学生的工作。鉴于我院可招收的转专业学生人数有限，具体工作安排如下：</span><span style=\"font-family:Arial, sans-serif;\"> </span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">2</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">13</span><span style=\"font-family:宋体;\">日</span><span style=\"font-family:Arial, sans-serif;\">~2</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">17</span><span style=\"font-family:宋体;\">日</span><span style=\"font-family:Arial, sans-serif;\">12:00</span><span style=\"font-family:宋体;\">：接收学生的报名材料原件（成绩单一份、排名证明一份、转专业申请两份）并审核；</span><span style=\"font-family:Arial, sans-serif;\"> </span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">2</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">20</span><span style=\"font-family:宋体;\">日：确定参加面试名单并通知学生；</span><span style=\"font-family:Arial, sans-serif;\"> </span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">2</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">21</span><span style=\"font-family:宋体;\">日：进行面试；</span><span style=\"font-family:Arial, sans-serif;\"> </span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">2</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">22</span><span style=\"font-family:宋体;\">日：确定接收名单并报校教务处公示。</span><span style=\"font-family:Arial, sans-serif;\"> </span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">材料提交地址：</span><span style=\"font-family:Arial, sans-serif;\">2-426 </span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">接收人：寿老师</span><span style=\"font-family:Arial, sans-serif;\"> </span></p>\r\n<p><span style=\"font-family:Arial, sans-serif;\">&nbsp;</span></p>\r\n<p align=\"right\" style=\"text-align:right;\"><span style=\"font-family:Arial, sans-serif;\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:宋体;\">信息与通信工程学院</span><span style=\"font-family:Arial, sans-serif;\"> </span></p>\r\n<p align=\"right\" style=\"text-align:right;\"><span style=\"font-family:Arial, sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2012/2/12</span></p>', '信息与通信工程学院关于开展接收2011级转专业学生的工作安排', '', '2014-12-18 10:25:18', '', '0', null, '0', null, '108');
INSERT INTO `t_content` VALUES ('26', '<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">信通院</span><span style=\"font-family:Arial, sans-serif;\">2015</span><span style=\"font-family:宋体;\">年接收推免生</span><span style=\"font-family:Arial, sans-serif;\"> </span><span style=\"font-family:宋体;\">复试环节包含两个部分：思想政治素质面试和业务复试。考生需先参加政治面试，之后再到所报导师组参加业务复试。</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">政治素质面试安排：</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">9</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">28</span><span style=\"font-family:宋体;\">日—</span><span style=\"font-family:Arial, sans-serif;\">30</span><span style=\"font-family:宋体;\">日，</span><span style=\"font-family:Arial, sans-serif;\">10</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">8</span><span style=\"font-family:宋体;\">日—</span><span style=\"font-family:Arial, sans-serif;\">10</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">9</span><span style=\"font-family:宋体;\">日</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">每天上午</span><span style=\"font-family:Arial, sans-serif;\">9:00-11:30</span><span style=\"font-family:宋体;\">，下午</span><span style=\"font-family:Arial, sans-serif;\">2:00-5:00</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">地点：</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;\">北京邮电大学信息网络中心</span><span style=\"font-family:Arial, sans-serif;\">211</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:Arial, sans-serif;\">215</span><span style=\"font-family:宋体;\">室。</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">（接到“复试通知”的考生，需先进入系统“接收复试通知”，后持本人身份证件，并另行准备一份《推免生申请表》，到信通院研究生教务科办理复试报到手续，缴纳</span><span style=\"font-family:Arial, sans-serif;\">100</span><span style=\"font-family:宋体;\">元复试费</span><span style=\"font-family:Arial, sans-serif;\">(</span><span style=\"font-family:宋体;\">持有研招办出具免复试费证明的考生除外</span><span style=\"font-family:Arial, sans-serif;\">)</span><span style=\"font-family:宋体;\">并在《推免生申请表》上加盖公章，方可参加复试（含本校参加推免的本科生）。政治面试</span><span style=\"font-family:Arial, sans-serif;\"> </span><span style=\"font-family:宋体;\">需在业务复试之前完成，学生可在以上时间内，携带盖有信通院研究生教务科红章的《推免生申请表》自行前往。</span><span style=\"font-family:Arial, sans-serif;\"> </span><span style=\"font-family:宋体;\">政治面试之后，考生需将此《推免生申请表》交至复试导师组留存。）</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p align=\"right\" style=\"margin-left:0cm;text-align:right;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">信息与通信工程学院</span></p>\r\n<p align=\"right\" style=\"margin-left:0cm;text-align:right;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">2014</span><span style=\"font-family:宋体;\">年</span><span style=\"font-family:Arial, sans-serif;\">9</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">26</span><span style=\"font-family:宋体;\">日</span></p>\r\n<br />', '信息与通信工程学院 关于2015年接收推荐免试研究生的政治面试安排', '', '2014-12-18 10:27:44', '', '0', null, '0', null, '109');
INSERT INTO `t_content` VALUES ('26', '<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">考生入场所带证件：</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:Arial, sans-serif;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-family:宋体;\">复试通知书、初试准考证、本人有效身份证件。</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">考试时间：</span><span style=\"font-family:Arial, sans-serif;\">2014</span><span style=\"font-family:宋体;\">年</span><span style=\"font-family:Arial, sans-serif;\">3</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">29</span><span style=\"font-family:宋体;\">日</span><span style=\"font-family:Arial, sans-serif;\"> </span><span style=\"font-family:宋体;\">上午</span><span style=\"font-family:Arial, sans-serif;\">10</span><span style=\"font-family:宋体;\">：</span><span style=\"font-family:Arial, sans-serif;\">00</span><span style=\"font-family:宋体;\">—</span><span style=\"font-family:Arial, sans-serif;\">11</span><span style=\"font-family:宋体;\">：</span><span style=\"font-family:Arial, sans-serif;\">00</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">考试方式：闭卷、笔试</span></p>\r\n<p style=\"margin-left:0cm;text-indent:21.0pt;\"><span style=\"font-family:宋体;\">笔试考号、考试地点、考试科目：见复试报到时发放的《复试通知书》</span></p>\r\n<p><span style=\"font-family:Arial, sans-serif;\">&nbsp;</span></p>\r\n<p align=\"right\" style=\"text-align:right;\"><span style=\"font-family:宋体;\">信息与通信工程学院</span></p>\r\n<p align=\"right\" style=\"text-align:right;\"><span style=\"font-family:Arial, sans-serif;\">2014</span><span style=\"font-family:宋体;\">年</span><span style=\"font-family:Arial, sans-serif;\">3</span><span style=\"font-family:宋体;\">月</span><span style=\"font-family:Arial, sans-serif;\">25</span><span style=\"font-family:宋体;\">日</span></p>\r\n<br />', '信息与通信工程学院招收2014年硕士研究生 专业笔试安排', '', '2014-12-18 10:29:22', '',